mapper.select:
INSERT INTO BOARD_REPLY
VALUES(
REPLY_NO_SEQ.NEXTVAL, REPLY_GROUPNO_SEQ.NEXTVAL, 1, 0, 'N', #{reply_content}, SYSDATE, #{board_no}, #{reply_nicname}
)

mapper.update:
UPDATE BOARD_REPLY
SET REPLY_CONTENT = #{reply_content}
WHERE REPLY_NO = #{reply_no}

UPDATE BOARD_REPLY
SET REPLY_DELFLAG = 'Y'
WHERE REPLY_NO = #{reply_no}
