mapper.select:
SELECT *
FROM (
		SELECT (ROW_NUMBER() OVER(PARTITION BY SUBSTR(CAL_MDATE, 1, 10) ORDER BY CAL_MDATE)) RN, CAL_NO, CAL_TITLE, CAL_CONTENT, CAL_MDATE, CAL_REGDATE, MEMBER_ID
		FROM MYCAL
		WHERE MEMBER_ID = #{member_id}
		AND SUBSTR(CAL_MDATE, 1, 7) = #{cal_mdate}
	 )
WHERE RN BETWEEN 1 AND 3

SELECT CAL_NO, CAL_TITLE, CAL_CONTENT, CAL_MDATE, CAL_REGDATE, MEMBER_ID
FROM MYCAL
WHERE MEMBER_ID = #{member_id}
AND REPLACE(CAL_MDATE, '-', '') = #{cal_mdate}

SELECT CAL_NO, CAL_TITLE, CAL_CONTENT, CAL_MDATE, CAL_REGDATE, MEMBER_ID
FROM MYCAL
WHERE CAL_NO = #{cal_no}

SELECT COUNT(*) 
FROM MYCAL
WHERE MEMBER_ID = #{member_id}
AND SUBSTR(CAL_MDATE, 1, 8) = #{cal_mdate}

mapper.insert:
INSERT INTO MYCAL
VALUES(
	CAL_NO_SEQ.NEXTVAL, #{cal_title}, #{cal_content}, #{cal_mdate}, SYSDATE, #{member_id}
)

INSERT INTO MYCAL
VALUES(
	CAL_NO_SEQ.NEXTVAL, '[완료]정기검진', '정기검진 진행 완료!', #{cal_mdate}, SYSDATE, #{member_id}
)

INSERT INTO MYCAL
VALUES(
	CAL_NO_SEQ.NEXTVAL, '[예정]정기검진', '정기검진 진행 예정일', #{cal_mdate}, SYSDATE, #{member_id}
)
